import React, { useState } from "react";
import {
  Building2,
  Mail,
  Lock,
  User,
  Phone,
  MapPin,
  Eye,
  EyeOff,
  CheckCircle,
} from "lucide-react";
import { login as authLogin } from "../utils/auth";

const AuthDialog = ({ isOpen, onClose, onAuthSuccess }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    companyName: "",
    cnpj: "",
    contactName: "",
    phone: "",
    address: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      if (isLogin) {
        // Chamar API de login
        // Exemplo: await login(formData.email, formData.password)
        // Simulação:
        setTimeout(() => {
          setLoading(false);
          authLogin({ email: formData.email });
          onAuthSuccess && onAuthSuccess({ email: formData.email });
          onClose && onClose();
        }, 1000);
      } else {
        // Chamar API de cadastro
        // Exemplo: await register(formData)
        setTimeout(() => {
          setLoading(false);
          authLogin({ email: formData.email });
          onAuthSuccess && onAuthSuccess({ email: formData.email });
          onClose && onClose();
        }, 1000);
      }
    } catch (err) {
      setError("Erro ao autenticar.");
      setLoading(false);
    }
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setFormData({
      email: "",
      password: "",
      confirmPassword: "",
      companyName: "",
      cnpj: "",
      contactName: "",
      phone: "",
      address: "",
    });
    setError("");
  };

  if (!isOpen) return null;

