import React, { useState } from "react";
import {
  Building2,
  Mail,
  Lock,
  User,
  Phone,
  MapPin,
  Eye,
  EyeOff,
  CheckCircle,
} from "lucide-react";
import { login as authLogin } from "../utils/auth";
import { AuthService } from "../services/authServices";
import { useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
const AuthDialog = ({ isOpen, onClose, onAuthSuccess }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [userType, setUserType] = useState("cliente"); // "cliente" ou "parceiro"
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    companyName: "",
    cnpj: "",
    contactName: "",
    phone: "",
    address: "",
    nome: "",
    cpf: "",
  });
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const [invalidFields, setInvalidFields] = useState([]);

  // Função para formatar telefone visualmente
  const formatPhone = (value) => {
    const digits = value.replace(/\D/g, "");
    if (digits.length <= 2) return digits;
    if (digits.length <= 7) return `(${digits.slice(0, 2)}) ${digits.slice(2)}`;
    if (digits.length <= 11)
      return `(${digits.slice(0, 2)}) ${digits.slice(2, 7)}-${digits.slice(
        7,
        11
      )}`;
    return `(${digits.slice(0, 2)}) ${digits.slice(2, 7)}-${digits.slice(
      7,
      11
    )}`;
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (name === "rememberMe") {
      setRememberMe(checked);
    } else if (name === "phone") {
      // Só aceita números e formata visualmente
      const digits = value.replace(/\D/g, "");
      setFormData((prev) => ({
        ...prev,
        phone: digits,
        phoneFormatted: formatPhone(digits),
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleUserTypeSwitch = (type) => {
    setUserType(type);
    setFormData(type === "cliente" ? initialCliente : initialParceiro);
    setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    // Validação de campos obrigatórios
    let requiredFields = [];
    if (isLogin) {
      requiredFields =
        userType === "cliente" ? ["email", "password"] : ["email", "password"];
    } else {
      requiredFields =
        userType === "cliente"
          ? [
              "razao_social",
              "cnpj",
              "nome_representante",
              "cpf",
              "email",
              "password",
              "confirmPassword",
              "telefone",
              "endereco",
              "cidade",
              "estado",
              "cep",
            ]
          : ["nome", "cpf", "email", "password", "confirmPassword", "phone"];
    }
    const emptyFields = requiredFields.filter(
      (field) => !formData[field] || formData[field].toString().trim() === ""
    );
    if (emptyFields.length > 0) {
      setInvalidFields(emptyFields);
      setError("Todos os campos obrigatórios devem ser preenchidos.");
      setLoading(false);
      return;
    } else {
      setInvalidFields([]);
    }

    try {
      if (isLogin) {
        let response;
        if (userType === "cliente") {
          response = await AuthService.loginCliente({
            email: formData.email,
            password: formData.password,
          });
        } else {
          response = await AuthService.loginParceiroSaas({
            email: formData.email,
            password: formData.password,
          });
        }
        if (response && response.token) {
          sessionStorage.setItem("token", response.token);
          sessionStorage.setItem("user", JSON.stringify(response.user));
          if (rememberMe) {
            localStorage.setItem("token", response.token);
            localStorage.setItem("user", JSON.stringify(response.user));
          }
          navigate("/");
          onAuthSuccess?.({ email: formData.email });
          onClose?.();
        }
      } else {
        // Cadastro
        if (userType === "cliente") {
          await AuthService.registerCliente({
            email: formData.email,
            password: formData.password,
            confirmPassword: formData.confirmPassword,
            razao_social: formData.razao_social,
            cnpj: formData.cnpj,
            nome_representante: formData.nome_representante,
            cpf: formData.cpf,
            telefone: formData.telefone,
            endereco: formData.endereco,
            cidade: formData.cidade,
            estado: formData.estado,
            cep: formData.cep,
          });
        } else {
          await AuthService.registerParceiroSaas({
            email: formData.email,
            password: formData.password,
            confirmPassword: formData.confirmPassword,
            nome: formData.nome,
            cpf: formData.cpf,
            phone: formData.phone,
          });
        }
        setTimeout(() => {
          setLoading(false);
          authLogin({ email: formData.email });
          onAuthSuccess?.({ email: formData.email });
          onClose?.();
        }, 1000);
      }
    } catch (error) {
      if (error.response) {
        const status = error.response.status;
        const responseData = error.response.data;
        switch (status) {
          case 400:
            setError(responseData.message || "E-mail e senha são obrigatórios");
            break;
          case 401:
            setError(responseData.message || "Usuário ou senha inválidos");
            break;
          case 402:
            navigate("/confirmar-email", {
              state: { email: formData.email },
            });
            break;
          case 403:
            setError(responseData.message || "E-mail não verificado");
            if (responseData.reason) {
              console.info("Motivo:", responseData.reason);
            }
            break;
          case 500:
            setError("Erro interno do servidor. Tente novamente mais tarde.");
            break;
          default:
            setError("Erro ao fazer login. Tente novamente.");
        }
      } else {
        setError("Erro de conexão. Verifique sua internet e tente novamente.");
      }
      console.error("Erro no login:", error);
    } finally {
      setLoading(false);
    }
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setFormData({
      email: "",
      password: "",
      confirmPassword: "",
      companyName: "",
      cnpj: "",
      contactName: "",
      phone: "",
      address: "",
    });
    setError("");
  };

  // Campos iniciais para cada tipo
  const initialCliente = {
    email: "",
    password: "",
    confirmPassword: "",
    razao_social: "",
    cnpj: "",
    nome_representante: "",
    cpf: "",
    telefone: "",
    endereco: "",
    cidade: "",
    estado: "",
    cep: "",
  };
  const initialParceiro = {
    email: "",
    password: "",
    confirmPassword: "",
    nome: "",
    cpf: "",
    phone: "",
    phoneFormatted: "",
  };

  return (
    <div className="min-h-screen w-full flex items-center justify-center bg-gray-50">
      <div className="bg-white max-w-md w-full m-4 rounded-2xl shadow-lg p-0 relative animate-fadeIn flex flex-col">
        <div className="flex justify-center mt-6 mb-2">
          <div className="flex rounded-full overflow-hidden border border-gray-200">
            <motion.button
              layout
              transition={{ type: "spring", stiffness: 400, damping: 30 }}
              className={`px-5 py-2 text-sm font-semibold focus:outline-none transition-colors duration-200 ${
                userType === "cliente"
                  ? "bg-black text-white"
                  : "bg-gray-100 text-gray-700"
              }`}
              onClick={() => handleUserTypeSwitch("cliente")}
            >
              Cliente
            </motion.button>
            <motion.button
              layout
              transition={{ type: "spring", stiffness: 400, damping: 30 }}
              className={`px-5 py-2 text-sm font-semibold focus:outline-none transition-colors duration-200 ${
                userType === "parceiro"
                  ? "bg-black text-white"
                  : "bg-gray-100 text-gray-700"
              }`}
              onClick={() => handleUserTypeSwitch("parceiro")}
            >
              Parceiro SaaS
            </motion.button>
          </div>
        </div>
        <div className="flex-1 p-8">
          <button
            onClick={() => (window.location.href = "/")}
            className="absolute top-4 right-4 text-gray-400 hover:text-black text-xl"
          >
            ×
          </button>
          {/* Logo/Header */}
